    get_value(@number);

    set_value(number);
}

// Should not take ownership and not modify the variable passed.
fn get_value(number: @Number) -> u32 {
    *number.value
}

// Should take ownership
fn set_value(mut number: Number) {
    let value = 2222222;
    number = Number { value };
    println!("Number is: {}", number.value);
}

