        fn generate_withdrawal_hash(
            self: @ContractState, caller: ContractAddress, bank_account: felt252, timestamp: u64,
        ) -> u256 {
            // Use Poseidon hash for proper cryptographic hashing
            let caller_felt: felt252 = caller.into();
            let timestamp_felt: felt252 = timestamp.into();

            let hash_state = PoseidonTrait::new()
                .update(caller_felt)
                .update(bank_account)
                .update(timestamp_felt);

            let hash_felt: felt252 = hash_state.finalize();
            hash_felt.into()
        }

        // Helper - calls transfer_from and asserts success
        fn transfer_tokens_with_approval(
            ref self: ContractState,
            token_address: ContractAddress,
            from: ContractAddress,
            to: ContractAddress,
            amount: u256,
        ) {
            let token_contract = IERC20Dispatcher { contract_address: token_address };

            // Some token implementations return bool; others revert on failure.
            // We check return value if provided and also allow reverts to propagate.
            let transfer_result: bool = token_contract.transfer_from(from, to, amount);
            // If the token returns false (instead of reverting), guard it here:
            assert(transfer_result, 'ERC20 transfer_from failed');
        }

    }
    }
