/// Interface for the Remittance Wallet contract.
/// Allows registering unique IDs to addresses, sending funds via IDs, retrieving mappings, and
/// Bitcoin bridging.
#[starknet::interface]
pub trait IRemittanceWallet<TContractState> {
    /// Register a unique ID (e.g., hashed phone/email) to the caller's address.
    fn register_id(ref self: TContractState, id: felt252);
    /// Send funds to an address mapped by ID.
    fn send_to_id(ref self: TContractState, id: felt252, amount: u256);
    /// Get the address associated with an ID.
    fn get_address_by_id(self: @TContractState, id: felt252) -> starknet::ContractAddress;
    /// Check if an ID is registered.
    fn is_id_registered(self: @TContractState, id: felt252) -> bool;
    /// Deposit BTC via bridging (integrates with Atomiq/Xverse).
    fn deposit_btc(ref self: TContractState, amount: u256);
    /// Withdraw BTC via bridging.
    fn withdraw_btc(ref self: TContractState, amount: u256);
}

/// Remittance Wallet contract for mapping IDs to addresses and facilitating transfers.
#[starknet::contract]
pub mod RemittanceWallet {
    use starknet::storage::*;
    use starknet::{ContractAddress, get_caller_address};

    #[storage]
    struct Storage {
        id_to_address: Map<felt252, ContractAddress>,
        registered_ids: Map<ContractAddress, felt252>,
    }

    #[event]
    #[derive(Drop, starknet::Event)]
    pub enum Event {
        IdRegistered: IdRegistered,
        TransferSent: TransferSent,
    }

    #[derive(Drop, starknet::Event)]
    pub struct IdRegistered {
        pub user: ContractAddress,
        pub id: felt252,
    }

    #[derive(Drop, starknet::Event)]
    pub struct TransferSent {
        pub from: ContractAddress,
        pub to: ContractAddress,
        pub amount: u256,
    }

    #[abi(embed_v0)]
    impl RemittanceWalletImpl of super::IRemittanceWallet<ContractState> {
        fn register_id(ref self: ContractState, id: felt252) {
            let caller = get_caller_address();
            assert(self.registered_ids.entry(caller).read() == 0, 'ID already registered');
            self.id_to_address.entry(id).write(caller);
            self.registered_ids.entry(caller).write(id);
            self.emit(Event::IdRegistered(IdRegistered { user: caller, id }));
        }

        fn send_to_id(ref self: ContractState, id: felt252, amount: u256) {
            let caller = get_caller_address();
            let recipient = self.id_to_address.entry(id).read();
            assert(recipient != 0.try_into().unwrap(), 'ID not registered');
            // Note: Actual transfer logic would integrate with ERC20 or native token.
            // For demo, emit event. In production, use transfer functions.
            self.emit(Event::TransferSent(TransferSent { from: caller, to: recipient, amount }));
        }

        fn get_address_by_id(self: @ContractState, id: felt252) -> ContractAddress {
            self.id_to_address.entry(id).read()
        }

        fn is_id_registered(self: @ContractState, id: felt252) -> bool {
            let addr = self.id_to_address.entry(id).read();
            addr != 0.try_into().unwrap()
        }

        fn deposit_btc(ref self: ContractState, amount: u256) {
            // Placeholder for Bitcoin bridging integration (e.g., call Atomiq contract).
            // In production: Interact with bridging contract to deposit BTC and mint equivalent on Starknet.
            let caller = get_caller_address();
            // Emit event for deposit (integrate with actual bridging).
