fn get_current_price(self: @ContractState, creation_id: u256) -> u256 {
    let creation = self.creations.read(creation_id);
    assert(creation.id != 0, 'Creation does not exist');

    // Bonding curve linaire : prix = base_price + (tokens_sold / total_supply) * (end_price - base_price)
    if creation.tokens_sold == 0 {
        return creation.base_price;
    }

    let price_range = creation.end_price - creation.base_price;
    let progress = (creation.tokens_sold * price_range) / creation.total_supply;

    creation.base_price + progress
}

