pub mod PayLink {
    use starknet::ContractAddress;
    use starknet::get_caller_address;
    use openzeppelin::token::erc20::interface::{IERC20Dispatcher, IERC20DispatcherTrait};

    #[storage]
    pub struct Storage {
        requests: Map<felt252, PaymentRequest>,
        next_id: felt252,
    }

    #[event]
    #[derive(Drop, starknet::Event)]
    pub enum Event {
        LinkCreated: LinkCreated,
        PaymentCompleted: PaymentCompleted,
    }

    #[derive(Drop, starknet::Event)]
    pub struct LinkCreated {
        request_id: felt252,
        recipient: ContractAddress,
        amount: u256,
        token_address: ContractAddress,
        memo: felt252,
    }

    #[derive(Drop, starknet::Event)]
    pub struct PaymentCompleted {
        request_id: felt252,
        payer: ContractAddress,
        amount: u256,
        token: ContractAddress,
    }

    #[constructor]
    fn constructor(ref self: ContractState) {
        self.next_id.write(1);
    }

    #[abi(embed_v0)]
    pub impl PayLinkImpl of super::IPayLink<ContractState> {
        fn create_link(ref self: ContractState, amount: u256, token_address: ContractAddress, recipient: ContractAddress, memo: felt252) -> felt252 {
            let id = self.next_id.read();
            self.next_id.write(id + 1);
            let request = PaymentRequest { amount, token_address, recipient, memo, paid: false };
            self.requests.write(id, request);
            self.emit(Event::LinkCreated(LinkCreated { request_id: id, recipient, amount, token_address, memo }));
            id
        }

        fn pay(ref self: ContractState, request_id: felt252) {
            let request = self.requests.read(request_id);
            assert!(!request.paid, "Already paid");
            let payer = get_caller_address();
            let token = IERC20Dispatcher { contract_address: request.token_address };
            token.transfer_from(payer, request.recipient, request.amount);
            let new_request = PaymentRequest { amount: request.amount, token_address: request.token_address, recipient: request.recipient, memo: request.memo, paid: true };
            self.requests.write(request_id, new_request);
            self.emit(Event::PaymentCompleted(PaymentCompleted { request_id, payer, amount: request.amount, token: request.token_address }));
        }

        fn get_request(self: @ContractState, request_id: felt252) -> (u256, ContractAddress, ContractAddress, felt252, bool) {
            let request = self.requests.read(request_id);
            (request.amount, request.token_address, request.recipient, request.memo, request.paid)
        }

        fn get_next_id(self: @ContractState) -> felt252 {
            self.next_id.read()
        }
    }
}

