    const VALIDATOR_ROLE: felt252 = selector!("VALIDATOR_ROLE");
    const ADMIN_ROLE: felt252 = selector!("ADMIN_ROLE");
    const REPORT_READER: felt252 = selector!("REPORT_READER");


    #[constructor]
    fn constructor(
        ref self: ContractState,
        erc20: ContractAddress,
        owner: ContractAddress,
        revenue_account: ContractAddress,
    ) {
        self.strk_token_address.write(erc20);
        self.ownable.initializer(owner);
        self.accesscontrol.initializer();
        self.accesscontrol._grant_role(VALIDATOR_ROLE, owner);
    self.fortichain_revenue_account.write(revenue_account); // Set the revenue account
    // default no certificate contract configured
    let zero: ContractAddress = 0.try_into().unwrap();
    self.certificate_nft_address.write(zero);
    }

    #[abi(embed_v0)]
    impl FortichainImpl of IFortichain<ContractState> {
        fn upload_project(
            ref self: ContractState,
            name: ByteArray,
            description: ByteArray,
            project_type: ByteArray,
            deadline: u64,
            repository_url: ByteArray,
            priority: felt252,
            smart_contract_address: ContractAddress,
            amount: u256 // Total audit cost in USDC
        ) -> u256 {
            let caller = get_caller_address();
            assert(caller != contract_address_const::<0>(), 'Zero address forbidden');
            let timestamp: u64 = get_block_timestamp();
            assert(deadline > get_block_timestamp(), 'Deadline not in future');
            assert(amount > 0, 'Amount cannot be zero');

            // 1. Check project owner's balance
            let token = self.strk_token_address.read();
            let erc20_dispatcher = super::IMockUsdcDispatcher { contract_address: token };
            assert(erc20_dispatcher.get_balance(caller) >= amount, 'Insufficient balance');

            // 2. Transfer the total amount from the owner to this contract (escrow)
            let contract_address = get_contract_address();
            let transfer_success = self.process_payment(caller, amount, contract_address);
            assert(transfer_success, 'Tokens transfer failed');

            // 3. Calculate the fund distribution
            let researcher_share = (amount * 60) / 100;
            let validator_share = (amount * 35) / 100;
            let fortichain_share = (amount * 5) / 100;

            // 4. Immediately transfer the 5% share to the FortiChain revenue account
            let revenue_account = self.fortichain_revenue_account.read();
            let revenue_payment_success = self
                .process_payment(contract_address, fortichain_share, revenue_account);
            assert(revenue_payment_success, 'Revenue payment failed');

            // 5. Create the project and escrow records
            let id: u256 = self.project_count.read() + 1;
            let project = Project {
                id,
                name,
                description,
                project_type,
                project_owner: caller,
                smart_contract_address,
                repository_url,
                priority,
                is_active: true,
                is_completed: false,
                created_at: timestamp,
                updated_at: timestamp,
                deadline,
                validator_paid: false,
                researchers_paid: false,
            };

            self.projects.write(id, project);
            self.project_count.write(id);
            self.in_progress_projects.write(id, true);
            self.user_projects.entry(caller).push(id);

            let escrow_id: u256 = self.escrows_count.read() + 1;
            let escrow = Escrow {
                id: escrow_id,
                project_id: id,
                projectOwner: caller,
                total_deposit: amount,
                researcher_share, // Store the 60% share
                validator_share, // Store the 35% share
                is_active: true,
                created_at: timestamp,
                updated_at: timestamp,
                validator_paid: false,
                researchers_paid: false,
            };

            self.escrows_count.write(escrow_id);
            self.escrows.write(escrow_id, escrow);
            self.project_escrows.write(id, escrow);

            self
                .emit(
                    Event::ProjectCreated(
                        ProjectCreated {
                            project_id: id, project_owner: caller, created_at: timestamp,
                        },
                    ),
                );
            self.emit(Event::EscrowCreated(EscrowCreated { escrow_id, owner: caller, amount }));

            id
        }

        fn create_username(ref self: ContractState, username: felt252) {
            let caller = get_caller_address();
            // Ensure the username is not empty
            assert(username != 0, 'Username cannot be empty');
            // Check that the user does not already have a username
            assert(self.researcher_usernames.read(caller) == 0, 'Username already set');
            // Enforce global uniqueness
            assert(self.username_owner.read(username) == 0.try_into().unwrap(), 'Username taken');

            self.researcher_usernames.write(caller, username);
            self.username_owner.write(username, caller);

            if !self.is_researcher_listed.read(caller) {
                self.researcher_addresses.push(caller);
                self.is_researcher_listed.write(caller, true);
            }
        }

        fn edit_project(
            ref self: ContractState,
            project_id: u256,
            description: ByteArray,
            repository_url: ByteArray
        ) -> bool {
            let caller = get_caller_address();
            let mut project = self.projects.read(project_id);

            // 1. Authenticate: Only the project owner can edit
            assert(project.project_owner == caller, 'Only project owner can edit');

            // 2. Condition: Project must be active and not completed
            assert(project.is_active && !project.is_completed, 'Project not editable');

            // 3. Condition: No validator should be assigned yet
            let assigned_validator = self.project_validators.read(project_id);
            assert(
                assigned_validator.validator_address == 0.try_into().unwrap(),
                'Validator already assigned'
            );

            // 4. Update project details
            project.description = description;
            project.repository_url = repository_url;
            project.updated_at = get_block_timestamp();
            self.projects.write(project_id, project);

            // 5. Emit event
            self
                .emit(
                    Event::ProjectEdited(
                        ProjectEdited {
                            project_id,
                            project_owner: caller,
                            edited_at: get_block_timestamp(),
                        },
                    ),
                );

            true
        }

