        fn vote_for_participant(
            ref self: ContractState,
            project_id: u256,
            participant_address: ContractAddress,
            rating: u8,
        ) {
            let caller = get_caller_address();
            let project = self.projects.read(project_id);

            // 1. Authenticate: Only the project owner can vote
            assert(project.project_owner == caller, 'Only project owner can vote');

            // 2. Condition: Ensure the project is completed before allowing votes
            assert(project.is_completed, 'Project must be completed');

            // 3. Validate: Ensure the rating is within a valid range (e.g., 1-5)
            assert(rating >= 1 && rating <= 5, 'Rating must be between 1 and 5');

            // 4. Ensure participant actually worked on this project
            let assigned_validator = self.project_validators.read(project_id).validator_address;
            let mut participated = false;
            if assigned_validator == participant_address {
                participated = true;
            } else {
                let (rep, _) = self.researchers_reports.read((participant_address, project_id));
                if rep.id > 0 {
                    participated = true;
                }
            }
            assert(participated, 'Participant not on project');

            // 5. Persist rating, one vote per participant per project (last write wins)
            self.owner_ratings.write((project_id, participant_address), rating);
            self.owner_votes_exists.write((project_id, participant_address), true);

            // 6. Emit the vote as an event for off-chain processing
            self
                .emit(
                    Event::VoteCast(
                        VoteCast {
                            project_id, voter: caller, participant: participant_address, rating,
                        },
                    ),
                );
        }

    // Permissionless close after deadline: closes project and credits payouts
    fn close_project(ref self: ContractState, project_id: u256) -> bool {
            let mut project = self.projects.read(project_id);
            assert(project.id > 0, PROJECT_NOT_FOUND);
            // Must be after deadline and not yet completed
            assert(project.deadline < get_block_timestamp(), CAN_ONLY_CLOSE_AFTER_DEADLINE);
            if project.is_completed {
                return false;
            }

            let timestamp: u64 = get_block_timestamp();
            project.is_active = false;
            project.is_completed = true;
            project.updated_at = timestamp;
            self.projects.write(project.id, project.clone());

            // Cache owner address to avoid potential move issues
            let owner_addr = project.project_owner;

            self.in_progress_projects.write(project_id, false);
            self.completed_projects.write(project_id, true);
            self.update_project_completion_status(project_id, true);

            // Mint certificate if NFT contract configured
            let nft_addr = self.certificate_nft_address.read();
            let zero: ContractAddress = 0.try_into().unwrap();
            if nft_addr != zero {
                let nft = ICertificateNFTDispatcher { contract_address: nft_addr };
                nft.mint(owner_addr, project_id);
                self.emit(
                    Event::CertificateIssued(
                        CertificateIssued {
                            project_id,
                            project_owner: owner_addr,
                            token_id: project_id,
                            issued_at: timestamp,
                        }
                    )
                );
            }

            self
                .emit(
                    Event::ProjectClosed(
                        ProjectClosed {
                            project_id,
                            project_owner: owner_addr,
                            closed_at: get_block_timestamp(),
                        },
                    ),
                );

            // Credit validator if assigned and not yet credited
            let mut escrow = self.project_escrows.read(project_id);
            let validator = self.project_validators.read(project_id);
            if validator.id > 0 && !escrow.validator_paid {
                let validator_pay = escrow.validator_share;
                escrow.validator_paid = true;
                self.validator_paid_amount.entry(validator.validator_address).write(validator_pay);
                self.escrows.write(escrow.id, escrow);
                self.project_escrows.write(project_id, escrow);
                // reflect on project to prevent double-credit attempts
                project.validator_paid = true;
                self.projects.write(project.id, project.clone());
                self.emit(Event::ValidatorPaid(ValidatorPaid { project_id, validator: validator.validator_address, amount: validator_pay, timestamp }));
            }

            // Credit approved researchers equally if not yet credited
            let mut escrow2 = self.project_escrows.read(project_id);
            if !escrow2.researchers_paid {
                let approved = self.get_list_of_approved_researchers(project_id);
                let n = approved.len();
                if n > 0 {
                    let total = escrow2.researcher_share;
                    let per = total / n.into();
                    let mut i = 0;
                    while i != n {
                        let addr = *approved.at(i);
                        if !self.paid_researchers.read((project_id, addr)) {
                            self.paid_researchers.write((project_id, addr), true);
                            let cur = self.researcher_paid_amount.read(addr);
                            self.researcher_paid_amount.write(addr, cur + per);
                        }
                        i += 1;
                    }
                    self.emit(Event::ResearchersPaid(ResearchersPaid { project_id, validator: validator.validator_address, amount: total, timestamp }));
                }
                escrow2.researchers_paid = true;
                self.escrows.write(escrow2.id, escrow2);
                self.project_escrows.write(project_id, escrow2);
                // reflect on project to prevent double-credit attempts
                project.researchers_paid = true;
                self.projects.write(project.id, project.clone());
            }

            // Mark certificate as issued (for getters)
            self.certificate_issued.write(project_id, true);
            true
        }

