            let caller = get_caller_address();
            // Self-access: allowed unconditionally
            if caller == user {
                let r = self.researcher_usernames.read(user);
                if r != 0 { return r; }
                let v = self.validator_usernames.read(user);
                return v;
            }
            // Admins can view without project context
            if self.accesscontrol.has_role(ADMIN_ROLE, caller) {
                let r = self.researcher_usernames.read(user);
                if r != 0 { return r; }
                let v = self.validator_usernames.read(user);
                return v;
            }
            // Project owner access requires a valid project id
            let project = self.projects.read(project_id);
            assert(project.id > 0, PROJECT_NOT_FOUND);
            if caller == project.project_owner {
                let r = self.researcher_usernames.read(user);
                if r != 0 { return r; }
                let v = self.validator_usernames.read(user);
                return v;
            }
            0
