        fn get_owner_votes_for_project(
            self: @ContractState, project_id: u256,
        ) -> Array<ParticipantRating> {
            let project = self.projects.read(project_id);
            assert(project.id > 0, PROJECT_NOT_FOUND);

            // Build the set of participants from (a) researchers who submitted reports for this
            // project and (b) the assigned validator. This avoids relying on username indexing.
            let reports = self.get_all_reports_for_project(project_id);
            let mut participants = ArrayTrait::new();

            let mut i = 0;
            while i < reports.len() {
                let r = reports.at(i).clone();
                let addr = r.researcher_address;
                // ensure uniqueness
                let mut exists = false;
                let mut k = 0;
                while k < participants.len() {
                    if *participants.at(k) == addr {
                        exists = true;
                        break;
                    }
                    k += 1;
                }
                if !exists {
                    participants.append(addr);
                }
                i += 1;
            }

            let assigned = self.project_validators.read(project_id).validator_address;
            if assigned != 0.try_into().unwrap() {
                // add assigned validator if not already included
                let mut exists = false;
                let mut k = 0;
                while k < participants.len() {
                    if *participants.at(k) == assigned {
                        exists = true;
                        break;
                    }
                    k += 1;
                }
                if !exists {
                    participants.append(assigned);
                }
            }

            // Collect ratings for participants that have an owner vote
            let mut res = ArrayTrait::new();
            let mut p = 0;
            while p < participants.len() {
                let addr = *participants.at(p);
                if self.owner_votes_exists.read((project_id, addr)) {
                    let rating = self.owner_ratings.read((project_id, addr));
                    res.append(ParticipantRating { participant: addr, rating });
                }
                p += 1;
            }
            res
        }

