    token: starknet::ContractAddress,
    total_amount: u256,
    remaining_amount: u256,
    num_packets: u32,
    claimed_count: u32,
    allocation_type: AllocationType,
    expiry: u64,
    packet_id: u256,
}

/// Lucky Drop Contract
#[starknet::contract]
mod LuckyDrop {
    use starknet::{
        ContractAddress, get_caller_address, get_contract_address, get_block_timestamp, get_block_number,
        storage::{StoragePointerReadAccess, StoragePointerWriteAccess, Map}
    };
    use openzeppelin::token::erc20::interface::{IERC20Dispatcher, IERC20DispatcherTrait};

    #[storage]
    struct Storage {
        red_packets: Map<u256, RedPacket>,
        packet_counter: u256,
        claimed: Map<(u256, ContractAddress), bool>,
    }

    #[event]
    #[derive(Drop, starknet::Event)]
    pub enum Event {
        RedPacketCreated: RedPacketCreated,
        RedPacketClaimed: RedPacketClaimed,
        RedPacketRefunded: RedPacketRefunded,
    }

    #[derive(Drop, starknet::Event)]
    pub struct RedPacketCreated {
        pub packet_id: u256,
        pub creator: ContractAddress,
        pub token: ContractAddress,
        pub total_amount: u256,
        pub num_packets: u32,
        pub allocation_type: AllocationType,
        pub expiry: u64,
    }

    #[derive(Drop, starknet::Event)]
    pub struct RedPacketClaimed {
        pub packet_id: u256,
        pub claimant: ContractAddress,
        pub amount: u256,
    }

    #[derive(Drop, starknet::Event)]
    pub struct RedPacketRefunded {
        pub packet_id: u256,
        pub creator: ContractAddress,
        pub amount: u256,
    }

    #[abi(embed_v0)]
    impl LuckyDropImpl of ILuckyDrop<ContractState> {
        fn create_red_packet(
            ref self: ContractState,
            token: ContractAddress,
            total_amount: u256,
            num_packets: u32,
            allocation_type: AllocationType,
            expiry: u64
        ) -> u256 {
            assert(total_amount > 0, 'Total amount must be > 0');
            assert(num_packets > 0, 'Num packets must be > 0');
            assert(expiry > get_block_timestamp(), 'Expiry must be in future');

            let caller = get_caller_address();
            let contract_address = get_contract_address();
            let packet_id = self.packet_counter.read() + 1;
            self.packet_counter.write(packet_id);

            // Transfer tokens from caller to contract
            let token_dispatcher = IERC20Dispatcher { contract_address: token };
            token_dispatcher.transfer_from(caller, contract_address, total_amount);

            let red_packet = RedPacket {
                creator: caller,
                token,
                total_amount,
                remaining_amount: total_amount,
                num_packets,
                claimed_count: 0,
                allocation_type,
                expiry,
                packet_id,
            };

            self.red_packets.write(packet_id, red_packet);

            self.emit(Event::RedPacketCreated(RedPacketCreated {
                packet_id,
                creator: caller,
                token,
                total_amount,
                num_packets,
                allocation_type,
                expiry,
            }));

            packet_id
        }

        fn claim_red_packet(ref self: ContractState, packet_id: u256, claimant: ContractAddress) -> u256 {
            let mut red_packet = self.red_packets.read(packet_id);
            assert(red_packet.packet_id == packet_id, 'Red packet does not exist');
            assert(get_block_timestamp() <= red_packet.expiry, 'Red packet expired');
            assert(red_packet.claimed_count < red_packet.num_packets, 'All packets claimed');
            assert(!self.claimed.read((packet_id, claimant)), 'Already claimed');

            let amount = self.calculate_claim_amount(red_packet, claimant);
            assert(amount <= red_packet.remaining_amount, 'Insufficient remaining amount');

            // Transfer tokens to claimant
            let token_dispatcher = IERC20Dispatcher { contract_address: red_packet.token };
            token_dispatcher.transfer(claimant, amount);

            // Update red packet
            red_packet.remaining_amount -= amount;
            red_packet.claimed_count += 1;
            self.red_packets.write(packet_id, red_packet);
            self.claimed.write((packet_id, claimant), true);

            self.emit(Event::RedPacketClaimed(RedPacketClaimed {
                packet_id,
                claimant,
                amount,
            }));

            amount
        }

        fn refund_red_packet(ref self: ContractState, packet_id: u256) {
            let mut red_packet = self.red_packets.read(packet_id);
            assert(red_packet.packet_id == packet_id, 'Red packet does not exist');
            assert(get_block_timestamp() > red_packet.expiry, 'Red packet not expired');
            assert(get_caller_address() == red_packet.creator, 'Only creator can refund');

            let refund_amount = red_packet.remaining_amount;
            if refund_amount > 0 {
                // Transfer remaining tokens to creator
                let token_dispatcher = IERC20Dispatcher { contract_address: red_packet.token };
                token_dispatcher.transfer(red_packet.creator, refund_amount);
            }

            // Mark as refunded by setting remaining to 0
            red_packet.remaining_amount = 0;
            self.red_packets.write(packet_id, red_packet);

            self.emit(Event::RedPacketRefunded(RedPacketRefunded {
                packet_id,
                creator: red_packet.creator,
                amount: refund_amount,
            }));
        }

        fn get_red_packet(self: @ContractState, packet_id: u256) -> (starknet::ContractAddress, starknet::ContractAddress, u256, u256, u32, u32, AllocationType, u64, u256) {
            let red_packet = self.red_packets.read(packet_id);
            (
                red_packet.creator,
                red_packet.token,
                red_packet.total_amount,
                red_packet.remaining_amount,
                red_packet.num_packets,
                red_packet.claimed_count,
                red_packet.allocation_type,
                red_packet.expiry,
                red_packet.packet_id,
            )
        }
    }

    #[generate_trait]
    impl InternalImpl of InternalTrait {
        fn calculate_claim_amount(self: @ContractState, red_packet: RedPacket, claimant: ContractAddress) -> u256 {
            match red_packet.allocation_type {
                AllocationType::Average => {
                    red_packet.total_amount / red_packet.num_packets.into()
                },
                AllocationType::Luck => {
                    // Simple randomness
                    let block_number = get_block_number();
                    let random_felt = (block_number.into() + claimant.into()) % red_packet.remaining_amount.try_into().unwrap();
                    let random_value = if random_felt == 0 { 1 } else { random_felt.into() };
                    random_value
                },
                AllocationType::TimeLimited => {
                    // For simplicity, same as average
                    red_packet.total_amount / red_packet.num_packets.into()
                },
            }
        }
    }
}

