use starknet::ContractAddress;

// Define the contract interface
#[starknet::interface]
pub trait IStarknetLotto<TContractState> {
    fn set_winning_number(ref self: TContractState, number: u64);
    fn get_winning_number(self: @TContractState) -> u64;
    fn get_owner(self: @TContractState) -> ContractAddress;
    fn buy_ticket(ref self: TContractState, number: u64);
    fn get_user_ticket(self: @TContractState, user: ContractAddress) -> u64;
    fn get_ticket_count(self: @TContractState) -> u64;
}

// Define the contract module
#[starknet::contract]
pub mod StarknetLotto {
    use starknet::ContractAddress;
    use starknet::storage::*;
    use starknet::get_caller_address;

    // Define storage variables
    #[storage]
    pub struct Storage {
        owner: ContractAddress,
        winning_number: u64,
        user_tickets: Map<ContractAddress, u64>,
        ticket_count: u64,
    }

    // Define events
    #[event]
    #[derive(Drop, starknet::Event)]
    pub enum Event {
        WinningNumberSet: WinningNumberSet,
        TicketBought: TicketBought,
    }

    #[derive(Drop, starknet::Event)]
    pub struct WinningNumberSet {
        pub number: u64,
        pub set_by: ContractAddress,
    }

    #[derive(Drop, starknet::Event)]
    pub struct TicketBought {
        pub user: ContractAddress,
        pub number: u64,
        pub ticket_count: u64,
    }

    // Constructor that receives the owner address
    #[constructor]
    fn constructor(ref self: ContractState, owner: ContractAddress) {
        self.owner.write(owner);
    }

    // Implement the contract interface
    #[abi(embed_v0)]
    pub impl StarknetLottoImpl of super::IStarknetLotto<ContractState> {
        // Set winning number (only owner access)
        fn set_winning_number(ref self: ContractState, number: u64) {
            let caller = get_caller_address();
            let owner = self.owner.read();
            assert!(caller == owner, "Only owner can set winning number");

            self.winning_number.write(number);
            self.emit(Event::WinningNumberSet(WinningNumberSet { 
                number, 
                set_by: caller 
            }));
        }

        // Get current winning number
        fn get_winning_number(self: @ContractState) -> u64 {
            self.winning_number.read()
        }

        // Get contract owner
        fn get_owner(self: @ContractState) -> ContractAddress {
            self.owner.read()
        }

        // Buy ticket with specific number (each user can only buy one ticket)
        fn buy_ticket(ref self: ContractState, number: u64) {
            let caller = get_caller_address();

            // Check if user already has a ticket
            let existing_ticket = self.user_tickets.entry(caller).read();
            assert!(existing_ticket == 0, "User already has a ticket");

            // Increment ticket count
            let current_count = self.ticket_count.read();
            self.ticket_count.write(current_count + 1);

            // Store user's ticket number
            self.user_tickets.entry(caller).write(number);

            // Emit event
            self.emit(Event::TicketBought(TicketBought {
                user: caller,
                number,
                ticket_count: current_count + 1,
            }));
        }

        // Get user's ticket number
        fn get_user_ticket(self: @ContractState, user: ContractAddress) -> u64 {
            self.user_tickets.entry(user).read()
        }

        // Get total ticket count
        fn get_ticket_count(self: @ContractState) -> u64 {
            self.ticket_count.read()
        }
    }
}

