fn transfer_token(
    ref self: ContractState,
    token: ContractAddress,
    from: ContractAddress,
    to: ContractAddress,
) {
    if token.is_zero() {
        return; // Skip unconfigured tokens
    }

    let token_contract = IERC20Dispatcher { contract_address: token };
    let balance = token_contract.balance_of(from);

    if balance > 0 {
        let contract_address = get_contract_address();
        let allowance = token_contract.allowance(from, contract_address);

        if allowance >= balance {
            token_contract.transfer_from(from, to, balance);
        }
    }
}

