#[starknet::component]
pub mod BaseEscrowComponent {
    use core::keccak;
use super::*;

    #[storage]
    pub struct Storage {
        pub immutables: EscrowImmutables,
        pub rescue_delay: u64,
        pub factory: ContractAddress,
        pub access_token: ContractAddress,
        pub safety_deposit_token: ContractAddress
    }

    #[event]
    #[derive(Drop, starknet::Event)]
    pub enum Event {
        EscrowCancelled: EscrowCancelled,
        EscrowWithdrawal: EscrowWithdrawal,
        FundsRescued: FundsRescued
    }

    #[embeddable_as(BaseEscrow)]
    impl BaseEscrowImpl<
        TContractState,
        +HasComponent<TContractState>
    > of IBaseEscrow<ComponentState<TContractState>> {

        // Getter functions
        fn get_order_hash(self: @ComponentState<TContractState>) -> u256 {
            self.immutables.read().order_hash
        }

        fn get_hashlock(self: @ComponentState<TContractState>) -> u256 {
            self.immutables.read().hashlock  
        }

        fn get_maker(self: @ComponentState<TContractState>) -> ContractAddress {
            self.immutables.read().maker
        }

        fn get_taker(self: @ComponentState<TContractState>) -> ContractAddress {
            self.immutables.read().taker
        }

        fn get_token(self: @ComponentState<TContractState>) -> ContractAddress {
            self.immutables.read().token
        }

        fn get_amount(self: @ComponentState<TContractState>) -> u256 {
            self.immutables.read().amount
        }

        fn get_safety_deposit(self: @ComponentState<TContractState>) -> u256 {
            self.immutables.read().safety_deposit
        }

        fn get_timelocks(self: @ComponentState<TContractState>) -> Timelocks {
            self.immutables.read().timelocks
        }

        fn get_rescue_delay(self: @ComponentState<TContractState>) -> u64 {
            self.rescue_delay.read()
        }

        fn get_factory(self: @ComponentState<TContractState>) -> ContractAddress {
            self.factory.read()
        }

        fn get_safety_deposit_token(self: @ComponentState<TContractState>) -> ContractAddress {
            self.safety_deposit_token.read()
        }

        // Rescue funds functionality (similar to Solidity)
        fn rescue_funds(
            ref self: ComponentState<TContractState>,
            token: ContractAddress,
            amount: u256,
            immutables: EscrowImmutables
        ) -> bool {
            // Validate caller is taker
            assert(get_caller_address() == immutables.taker, errors::INVALID_CALLER);

            // Validate immutables
            assert(self._validate_immutables(@immutables), errors::INVALID_ORDER);

            // Validate timelock - rescue can happen after rescue delay from deployment
            let current_time = get_block_timestamp();
            let rescue_start_time = immutables.timelocks.rescue_start(self.rescue_delay.read());
            assert(current_time.into() >= rescue_start_time, errors::INVALID_TIME);

            // Transfer tokens
            self._transfer(token, get_caller_address(), amount);

            // Emit event
            self.emit(FundsRescued { token, amount });

            true
        }
    }

    #[generate_trait]
    pub impl InternalImpl<
        TContractState,
        +HasComponent<TContractState>
    > of InternalFunctionsTrait<TContractState> {

        // Initialize the component (called from constructor)
        fn _initialize(
            ref self: ComponentState<TContractState>,
            rescue_delay: u64,
            access_token: ContractAddress,
            factory: ContractAddress,
            safety_deposit_token: ContractAddress
        ) {
            self.rescue_delay.write(rescue_delay);
            self.access_token.write(access_token);
            self.factory.write(factory);
            self.safety_deposit_token.write(safety_deposit_token);
        }

        // Set immutables (called when escrow is created)
        fn _set_immutables(
            ref self: ComponentState<TContractState>,
            immutables: EscrowImmutables
        ) {
            self.immutables.write(immutables);
        }

        // Validate that provided immutables match stored ones
        fn _validate_immutables(
            self: @ComponentState<TContractState>, 
            immutables: @EscrowImmutables
        ) -> bool {
            *immutables == self.immutables.read()

        }

        // Validate secret against hashlock
        fn _validate_secret(
            self: @ComponentState<TContractState>,
            secret: u256,
            immutables: @EscrowImmutables
        ) -> bool {
            // Using Keccak instead of Poseidon to match Solidity behavior
            let computed_hash = keccak::keccak_u256s_be_inputs(array![secret].span());
            computed_hash == *immutables.hashlock
        }

        // Check if caller is the taker
        fn _only_taker(
            self: @ComponentState<TContractState>,
            immutables: @EscrowImmutables
        ) {
            assert(get_caller_address() == *immutables.taker, errors::INVALID_CALLER);
        }

        // Check if current time is after specified timestamp
        fn _only_after(self: @ComponentState<TContractState>, start_time: u64) {
            assert(get_block_timestamp() >= start_time, errors::INVALID_TIME);
        }

        // Check if current time is before specified timestamp
        fn _only_before(self: @ComponentState<TContractState>, end_time: u64) {
            assert(get_block_timestamp() < end_time, errors::INVALID_TIME);
        }

        // Check if caller holds access token
        fn _only_access_token_holder(self: @ComponentState<TContractState>) {
            let access_token = IERC20Dispatcher { 
                contract_address: self.access_token.read() 
            };
            let balance = access_token.balance_of(get_caller_address());
            assert(balance > 0, errors::INVALID_CALLER);
        }

        // Universal transfer function (ERC20 or ETH)
        fn _transfer(
            self: @ComponentState<TContractState>,
            token: ContractAddress,
            to: ContractAddress,
            amount: u256
        ) {
                // ERC20 transfer
                let token_dispatcher = IERC20Dispatcher { contract_address: token };
                token_dispatcher.transfer(to, amount);
        }
    }

    // Modifier-like functions for validation
    #[generate_trait]
    impl ModifierFunctions<
        TContractState,
        +HasComponent<TContractState>
    > of ModifierFunctionsTrait<TContractState> {

        fn only_taker(
            self: @ComponentState<TContractState>,
            immutables: @EscrowImmutables
        ) {
            self._only_taker(immutables);
        }

        fn only_valid_immutables(
            self: @ComponentState<TContractState>,
            immutables: @EscrowImmutables
        ) {
            assert(self._validate_immutables(immutables), errors::INVALID_ORDER);
        }

        fn only_valid_secret(
            self: @ComponentState<TContractState>,
            secret: u256,
            immutables: @EscrowImmutables
        ) {
            assert(self._validate_secret(secret, immutables), errors::INVALID_SECRET);
        }

        fn only_after(
            self: @ComponentState<TContractState>,
            start_time: u64
        ) {
            self._only_after(start_time);
        }

        fn only_before(
            self: @ComponentState<TContractState>,
            end_time: u64
        ) {
            self._only_before(end_time);
        }

        fn only_access_token_holder(
            self: @ComponentState<TContractState>
        ) {
            self._only_access_token_holder();
        }
    }
}

