/// Validation data structure to store the last validated index and secret hash
#[derive(Copy, Drop, Serde, starknet::Store)]
pub struct ValidationData {
    pub index: u256,
    pub secret_hash: u256,
}

/// Taker data structure containing proof and validation information
#[derive(Drop, Serde)]
pub struct TakerData {
    pub proof: Array<u256>,
    pub idx: u256,
    pub secret_hash: u256,
}

/// Merkle Storage Invalidator interface
#[starknet::interface]
pub trait IMerkleStorageInvalidator<TContractState> {
    /// Returns the last validated data for a given key
    fn get_last_validated(self: @TContractState, key: u256) -> ValidationData;

    /// Validates and stores merkle proof data
    fn validate_merkle_proof(
        ref self: TContractState,
        order: EscrowImmutables,
        taker_data: TakerData,
        root: u256
    ) -> bool;

    /// Checks if a secret hash has been used for a specific order
    fn is_secret_used(self: @TContractState, order_hash: u256, secret_hash: u256) -> bool;
}

#[starknet::component]
mod MerkleStorageInvalidatorComponent {
    use super::*;

    #[storage]
    struct Storage {
        /// Maps key (hash of order_hash + root) to ValidationData
        last_validated: Map<u256, ValidationData>,
        /// Maps order_hash to used secret hashes for quick lookup
        used_secrets: Map<(u256, u256), bool>, // (order_hash, secret_hash) -> bool
    }

    #[event]
    #[derive(Drop, starknet::Event)]
    enum Event {
        SecretValidated: SecretValidated,
        InvalidProof: InvalidProof,
    }

    #[derive(Drop, starknet::Event)]
    struct SecretValidated {
        order_hash: u256,
        secret_hash: u256,
        index: u256,
    }

    #[derive(Drop, starknet::Event)]
    struct InvalidProof {
        order_hash: u256,
        provided_root: u256,
        calculated_root: u256,
    }

    mod Errors {
        pub const INVALID_PROOF: felt252 = 'Invalid merkle proof';
        pub const SECRET_ALREADY_USED: felt252 = 'Secret already used';
        pub const INVALID_INDEX: felt252 = 'Invalid index';
    }

    #[embeddable_as(MerkleStorageInvalidator)]
    impl MerkleStorageInvalidatorImpl<
        TContractState,
        +HasComponent<TContractState>
    > of IMerkleStorageInvalidator<ComponentState<TContractState>> {

        fn get_last_validated(self: @ComponentState<TContractState>, key: u256) -> ValidationData {
            self.last_validated.entry(key).read()
        }

        fn validate_merkle_proof(
            ref self: ComponentState<TContractState>,
            order: EscrowImmutables,
            taker_data: TakerData,
            root: u256
        ) -> bool {
            let key = self._generate_key(order.order_hash, root);

            // Check if this secret has already been used
            assert(
                !self.used_secrets.entry((order.order_hash, taker_data.secret_hash)).read(),
                Errors::SECRET_ALREADY_USED
            );

            // Get last validated data to ensure sequential usage
            let last_validation = self.last_validated.entry(key).read();

            // Ensure sequential index usage (next index should be last_index + 1 or 0 for first use)
            if last_validation.index != 0 {
                assert(taker_data.idx == last_validation.index, Errors::INVALID_INDEX);
            }

            // Create leaf hash from index and secret hash using Keccak
            let leaf_hash = self._create_leaf_hash(taker_data.idx, taker_data.secret_hash);

            // Verify merkle proof using Keccak
            let calculated_root = self._verify_merkle_proof(leaf_hash, taker_data.proof.span());

            // Compare with provided root (shortened)
            if calculated_root != root {
                self.emit(Event::InvalidProof(InvalidProof {
                    order_hash: order.order_hash,
                    provided_root: root,
                    calculated_root
                }));
                assert(false, Errors::INVALID_PROOF);
            }

            // Update storage
            let new_validation_data = ValidationData {
                index: taker_data.idx + 1,
                secret_hash: taker_data.secret_hash
            };

            self.last_validated.entry(key).write(new_validation_data);
            self.used_secrets.entry((order.order_hash, taker_data.secret_hash)).write(true);

            // Emit success event
            self.emit(Event::SecretValidated(SecretValidated {
                order_hash: order.order_hash,
                secret_hash: taker_data.secret_hash,
                index: taker_data.idx
            }));

            true
        }

        fn is_secret_used(
            self: @ComponentState<TContractState>, 
            order_hash: u256, 
            secret_hash: u256
        ) -> bool {
            self.used_secrets.entry((order_hash, secret_hash)).read()
        }
    }

    #[generate_trait]
    impl InternalFunctions<
        TContractState,
        +HasComponent<TContractState>
    > of InternalFunctionsTrait<TContractState> {

        /// Generates a unique key from order hash and shortened root using Keccak256
        fn _generate_key(
            self: @ComponentState<TContractState>,
            order_hash: u256,
            root: u256
        ) -> u256 {

            // Use Keccak256 to generate key (matching Solidity behavior)
            keccak_u256s_be_inputs(array![order_hash, root].span())
        }

        /// Creates leaf hash from index and secret hash using Keccak256
        fn _create_leaf_hash(
            self: @ComponentState<TContractState>,
            index: u256,
            secret_hash: u256
        ) -> u256 {
            keccak_u256s_be_inputs(array![index, secret_hash].span())
        }

        /// Verifies merkle proof using Keccak256 hash (matching Solidity behavior)
        fn _verify_merkle_proof(
            self: @ComponentState<TContractState>,
            leaf: u256,
            proof: Span<u256>
        ) -> u256 {
            let mut current_hash = leaf;
            let mut i: usize = 0;

            while i != proof.len() {
                let proof_element = *proof.at(i);
                current_hash = self._hash_pair(current_hash, proof_element);
                i += 1;
            };

            current_hash
        }

        /// Hash two elements using Keccak256, ordering them consistently
        fn _hash_pair(
            self: @ComponentState<TContractState>,
            a: u256,
            b: u256
        ) -> u256 {
            if a < b {
                keccak_u256s_be_inputs(array![a, b].span())
            } else {
                keccak_u256s_be_inputs(array![b, a].span())
            }
        }
    }
}

