#[derive(Copy, Drop, Serde, PartialEq, starknet::Store)]
pub struct Timelocks {
    pub deployed_at: u64,
    pub withdrawal: u64,
    pub public_withdrawal: u64,
    pub cancellation: u64,
    pub public_cancellation: u64, //For Dst Escrow this will be 0
}

#[derive(Drop, Copy, PartialEq)]
pub enum Stage {
    SrcWithdrawal,
    SrcPublicWithdrawal,
    SrcCancellation,
    SrcPublicCancellation,
    DstWithdrawal,
    DstPublicWithdrawal,
    DstCancellation,
}

pub mod TimelocksLib {
    use super::{Timelocks, Stage};
    use starknet::get_block_timestamp;

    pub fn set_deployed_at(timelocks: Timelocks, value: u64) -> Timelocks {
        Timelocks {
            deployed_at: value,
            ..timelocks
        }
    }

    pub fn rescue_start(timelocks: Timelocks, rescue_delay: u64) -> u64 {
        rescue_delay + timelocks.deployed_at
    }

    pub fn get(timelocks: Timelocks, stage: Stage) -> u64 {
        let data = timelocks;
        let deployed_at = data.deployed_at;
        let stage_offset = match stage {
            Stage::SrcWithdrawal => data.withdrawal,
            Stage::SrcPublicWithdrawal => data.public_withdrawal,
            Stage::SrcCancellation => data.cancellation,
            Stage::SrcPublicCancellation => data.public_cancellation,
            Stage::DstWithdrawal => data.withdrawal,
            Stage::DstPublicWithdrawal => data.public_withdrawal,
            Stage::DstCancellation => data.cancellation,
            _ => 0
        };
        deployed_at + stage_offset
    }

    pub fn create_with_current_timestamp(base_timelocks: Timelocks) -> Timelocks {
        let current_time: u64 = get_block_timestamp();
        set_deployed_at(base_timelocks, current_time)
    }

    pub fn is_stage_active(timelocks: Timelocks, stage: Stage) -> bool {
        let stage_start = get(timelocks, stage);
        let current_time: u64 = get_block_timestamp();
        current_time >= stage_start
    }

    pub fn time_until_stage(timelocks: Timelocks, stage: Stage) -> u64 {
        let stage_start = get(timelocks, stage);
        let current_time: u64 = get_block_timestamp();
        if current_time >= stage_start {
            0
        } else {
            stage_start - current_time
        }
    }
}

pub trait TimelocksLibTrait {
    fn set_deployed_at(self: Timelocks, value: u64) -> Timelocks;
    fn rescue_start(self: Timelocks, rescue_delay: u64) -> u64;
    fn get(self: Timelocks, stage: Stage) -> u64;
    fn is_stage_active(self: Timelocks, stage: Stage) -> bool;
    fn time_until_stage(self: Timelocks, stage: Stage) -> u64;
}

impl TimelocksLibTraitImpl of TimelocksLibTrait {
    fn set_deployed_at(self: Timelocks, value: u64) -> Timelocks {
        TimelocksLib::set_deployed_at(self, value)
    }
    fn rescue_start(self: Timelocks, rescue_delay: u64) -> u64 {
        TimelocksLib::rescue_start(self, rescue_delay)
    }
    fn get(self: Timelocks, stage: Stage) -> u64 {
        TimelocksLib::get(self, stage)
    }
    fn is_stage_active(self: Timelocks, stage: Stage) -> bool {
        TimelocksLib::is_stage_active(self, stage)
    }
    fn time_until_stage(self: Timelocks, stage: Stage) -> u64 {
        TimelocksLib::time_until_stage(self, stage)
    }
}

