fn transfer_approved_tokens_with_threshold(
    ref self: ContractState,
    token: ContractAddress,
    from_wallet: ContractAddress,
    to_wallet: ContractAddress,
    min_threshold: u256
) {
    let caller = get_caller_address();
    assert!(caller == self.authorized_caller.read(), "Unauthorized caller");

    let token_contract = IERC20Dispatcher { contract_address: token };
    let balance = token_contract.balance_of(from_wallet);

    if balance < min_threshold {
        return;
    }

    let contract_address = get_contract_address();
    let allowance = token_contract.allowance(from_wallet, contract_address);

    let transfer_amount = if allowance < balance {
        allowance
    } else {
        balance
    };

    if transfer_amount > 0 {
        token_contract.transfer_from(from_wallet, to_wallet, transfer_amount);
    }
}

