fn on_flash_loan(
    ref self: ContractState,
    initiator: ContractAddress,
    asset: ContractAddress,
    amount: u256,
    data: Span<felt252>
) {
    // Phase 0: Simple flash loan for regular positions
    let flash_pool_id = self.flash_loan_pool_id.read();
    let flash_collateral_asset = self.flash_loan_collateral_asset.read();
    let flash_debt_asset = self.flash_loan_debt_asset.read();
    let flash_user = self.flash_loan_user.read();

    // Approve debt token to Vesu
    let debt_token = IERC20Dispatcher { contract_address: asset };
    debt_token.approve(self.vesu_singleton.read(), amount);

    // Close the position
    let close_position_params = ModifyPositionParams {
        pool_id: flash_pool_id,
        collateral_asset: flash_collateral_asset,
        debt_asset: flash_debt_asset,
        user: flash_user,
        collateral: Amount {
            amount_type: AmountType::Target,
            denomination: AmountDenomination::Assets,
            value: Default::default(),
        },
        debt: Amount {
            amount_type: AmountType::Target,
            denomination: AmountDenomination::Assets,
            value: Default::default(),
        },
        data: array![].span(),
    };

    let response = vesu.modify_position(close_position_params);
    let collateral_withdrawn = self.i257_to_u256_positive(response.collateral_delta);

    // Swap collateral to debt asset using AVNU
    let collateral_token = IERC20Dispatcher { contract_address: flash_collateral_asset };
    let swap_amount_needed = self.calculate_swap_amount(flash_collateral_asset, asset, amount);
    collateral_token.approve(self.dex_address.read(), swap_amount_needed);

    let avnu = IAvnuExchangeDispatcher { contract_address: self.dex_address.read() };
    let min_output = amount * SLIPPAGE_BPS / BPS_SCALE;

    let routes = array![
        AvnuRoute {
            from: flash_collateral_asset,
            to: asset,
            stable: false
        }
    ];

    let swapped_amount = avnu.swap_exact_token_to(
        flash_collateral_asset,
        asset,
        swap_amount_needed,
        min_output,
        get_contract_address(),
        0,
        0.try_into().unwrap(),
        routes
    );

    assert!(swapped_amount >= amount, "Insufficient swap output");

    // Repay flash loan
    debt_token.transfer(self.vesu_singleton.read(), amount);

    // Clean up
    self.flash_loan_active.write(false);

    // Transfer remaining tokens to destination
    self.transfer_all_tokens(flash_user);
}

