fn emergency_recovery(ref self: ContractState, params: ModifyPositionParams) {
    let caller = get_caller_address();
    assert!(caller == self.authorized_caller.read(), 'Unauthorized caller');

    // Store flash loan data
    self.flash_loan_active.write(true);
    self.flash_loan_user.write(params.user);
    self.flash_loan_pool_id.write(params.pool_id);
    self.flash_loan_collateral_asset.write(params.collateral_asset);
    self.flash_loan_debt_asset.write(params.debt_asset);

    // Calculate debt amount from params
    let debt_amount = if params.debt.amount_type == 1 {
        // ALL type - need to get current debt
        let vesu = IVesuSingletonDispatcher { contract_address: self.vesu_singleton.read() };
        let (_collateral, debt) = vesu.position(params.pool_id, params.user);
        debt
    } else {
        params.debt.value
    };

    // Request flash loan for debt amount
    let vesu = IVesuSingletonDispatcher { contract_address: self.vesu_singleton.read() };
    vesu.flash_loan(
        get_contract_address(),
        params.debt_asset,
        debt_amount,
        array![]
    );
}

fn on_flash_loan(ref self: ContractState, sender: ContractAddress, asset: ContractAddress, amount: u256, data: Span<felt252>) -> bool {
    assert!(self.flash_loan_active.read(), 'Unexpected flash loan');
    assert!(sender == self.vesu_singleton.read(), 'Invalid flash loan sender');

    // Use flash loaned tokens to close position
    let vesu = IVesuSingletonDispatcher { contract_address: sender };
    let params = ModifyPositionParams {
        pool_id: self.flash_loan_pool_id.read(),
        collateral_asset: self.flash_loan_collateral_asset.read(),
        debt_asset: self.flash_loan_debt_asset.read(),
        user: self.flash_loan_user.read(),
        collateral: Amount { amount_type: 1, denomination: 0, value: 0 }, // ALL
        debt: Amount { amount_type: 1, denomination: 0, value: 0 }, // ALL
        data: array![]
    };

    // Close position - repays debt and withdraws collateral
    let response = vesu.modify_position(params);

    // Swap collateral to debt token
    let collateral_received = response.collateral_delta;
    let swap_amount_needed = self.calculate_swap_amount(amount);

    let dex = IDexDispatcher { contract_address: self.dex_address.read() };
    let swapped_amount = dex.swap(
        self.flash_loan_collateral_asset.read(),
        asset,
        collateral_received,
        swap_amount_needed
    );

    assert!(swapped_amount >= amount, 'Insufficient swap output');

    // Approve Vesu to take back the flash loan
    let debt_token = IERC20Dispatcher { contract_address: asset };
    debt_token.approve(sender, amount);

    self.flash_loan_active.write(false);
    true
}

