def modular_inverse(n, prime):
    return pow(n, -1, prime)

def ec_add(p1, p2):
    """Suma dos puntos en la curva elptica de Starknet."""
    if p1 is None: return p2
    if p2 is None: return p1
    x1, y1 = p1
    x2, y2 = p2
    if x1 == x2 and y1 != y2: return None
    if x1 == x2: return ec_double(p1)

    m = ((y2 - y1) * modular_inverse(x2 - x1, FIELD_PRIME)) % FIELD_PRIME
    x3 = (m * m - x1 - x2) % FIELD_PRIME
    y3 = (m * (x1 - x3) - y1) % FIELD_PRIME
    return x3, y3

def ec_double(p):
    """Dobla un punto en la curva elptica de Starknet."""
    if p is None: return None
    x, y = p
    if y == 0: return None

    m = ((3 * x * x + ALPHA) * modular_inverse(2 * y, FIELD_PRIME)) % FIELD_PRIME
    x3 = (m * m - 2 * x) % FIELD_PRIME
    y3 = (m * (x - x3) - y) % FIELD_PRIME
    return x3, y3

def pedersen_hash(x: int, y: int) -> int:
    """
    Calcula el hash de Pedersen de dos elementos del campo de forma correcta.
    """
    if not (0 <= x < FIELD_PRIME and 0 <= y < FIELD_PRIME):
        raise ValueError("Los elementos del hash deben estar en el campo de Starknet.")

    current_point = SHIFT_POINT
    # Este bucle es una simplificacin, la implementacin completa itera sobre 252 bits.
    for i, point in enumerate(POINTS):
        if (x >> i) & 1:
            current_point = ec_add(current_point, point)
        if (y >> i) & 1:
            # En la implementacin real, se usara un conjunto diferente de puntos para 'y'
            # Para este ejemplo, reutilizamos los puntos con un pequeo cambio.
            offset_point = (point[0], (point[1] * 2) % FIELD_PRIME)
            current_point = ec_add(current_point, offset_point)

    return current_point[0]

def hash_array(arr: list[int]) -> int:
    """
    Calcula el hash de Pedersen para un array de felts.
    """
    current_hash = 0
    for item in arr:
        current_hash = pedersen_hash(current_hash, item)

    # El hash final incluye la longitud del array
    return pedersen_hash(current_hash, len(arr))

def str_to_felt(text: str) -> int:
    """
    Convierte un string corto a su representacin en felt.
    """
    if len(text) > 31:
        raise ValueError("El texto es demasiado largo para ser un shortstring de Starknet.")
    return int.from_bytes(text.encode('utf-8'), 'big')

def get_type_hash(typed_data: dict, type_name: str) -> int:
    """
    Calcula el hash de la definicin de un tipo en SNIP-12.
    Ej: "StarkNetDomain(name:shortstring,chainId:shortstring)"
    """
    type_def = typed_data['types'][type_name]

    # Construir el string de la definicin
    members_str = ",".join(f"{member['name']}:{member['type']}" for member in type_def)
    full_type_str = f"{type_name}({members_str})"

    # En una implementacin real, se usara starknet_keccak. Usamos un hash simple como placeholder.
    from hashlib import sha256
    return int.from_bytes(sha256(full_type_str.encode()).digest(), 'big') % FIELD_PRIME

def get_struct_hash(typed_data: dict, type_name: str, data: dict) -> int:
    """
    Calcula el hash de una instancia de un struct en SNIP-12.
