def ec_mult(point, scalar):
    """Multiplicacin de un punto por un escalar (doble y suma)."""
    if scalar == 0:
        return None

    result = None
    current = point
    while scalar > 0:
        if scalar & 1:
            result = ec_add(result, current)
        current = ec_double(current)
        scalar >>= 1
    return result

def verify_signature(message_hash: int, signature: list[int], public_key: int) -> bool:
    """
    Verifica una firma ECDSA en la curva de Starknet.
    """
    r, s = signature
    if not (1 <= r < CURVE_ORDER and 1 <= s < CURVE_ORDER):
        return False

    s_inv = modular_inverse(s, CURVE_ORDER)

    # Calcular u1 = message_hash * s_inv (mod N)
    u1 = (message_hash * s_inv) % CURVE_ORDER

    # Calcular u2 = r * s_inv (mod N)
    u2 = (r * s_inv) % CURVE_ORDER

    # Calcular punto P = u1*G + u2*Q
    # donde G es el punto generador y Q es la clave pblica (un punto en la curva)
    # Para Starknet, la clave pblica (e.g., wallet_address) es la coordenada x del punto Q.
    # La coordenada y debe ser calculada. Esto es una simplificacin.
    # Una implementacin completa necesitara una funcin para obtener el punto Q de la clave pblica x.

