                                                             # pero estructura el flujo. La clave pblica no es el escalar.

    p1 = ec_mult(GENERATOR_POINT, u1)
    p2 = ec_mult(public_key_point, u2)

    point = ec_add(p1, p2)

    if point is None:
        return False

    # La firma es vlida si la coordenada x del punto calculado es igual a r
    return point[0] == r

